CREATE TABLE IF NOT EXISTS `educacion_testing`.`password_recovery_admins` (`user_id` INT NOT NULL , `hash` VARCHAR( 32 ) NOT NULL , `expiration_date` INT( 14 ) NOT NULL);
CREATE TABLE IF NOT EXISTS `educacion_testing`.`users` ( `id` int(11) NOT NULL AUTO_INCREMENT, `version` VARCHAR(20) NOT NULL, `email` varchar(255) NOT NULL, `password` varchar(32) NOT NULL, `name` varchar(100) NOT NULL, `rol` varchar(100) NULL, `price` varchar(15) NOT NULL, `weekend_price` varchar(15) NOT NULL, `out_academy_price` varchar(15) NOT NULL, `proveedor_pac` varchar(15) NOT NULL, `blocked` tinyint(1) NOT NULL, PRIMARY KEY (`id`), UNIQUE KEY (`email`));
CREATE TABLE IF NOT EXISTS `educacion_testing`.`password_recovery` (`user_id` INT NOT NULL , `hash` VARCHAR( 32 ) NOT NULL , `expiration_date` INT( 14 ) NOT NULL);
CREATE TABLE IF NOT EXISTS `educacion_testing`.`activity_log` (`id` int(11) NOT NULL AUTO_INCREMENT, `user` text NOT NULL, `action` varchar(255) NOT NULL, `object` text, `fecha` INT NOT NULL, `ip_address` varchar(15) NOT NULL, PRIMARY KEY (`id`));
CREATE TABLE IF NOT EXISTS `educacion_testing`.`cursos` ( `id` int(11) NOT NULL AUTO_INCREMENT, `nombre` varchar(255) NOT NULL, `horas` int(11) NOT NULL, `hora_inicio` varchar(10) NOT NULL, `hora_fin` varchar(10) NOT NULL, `fecha_inicio` int(10) NOT NULL, `fecha_fin` int(10) NOT NULL, `cupos_minimo` int(5) NOT NULL, `cupos_maximo` int(5) NOT NULL, `precio_prematricula` float DEFAULT NULL, `estado` varchar(50) NOT NULL, `version` varchar(20) NOT NULL, `producto_pac` varchar(50) NOT NULL, `sucursal` varchar(100) NOT NULL, `bodega` varchar(5) NOT NULL, `profesor` int(11) NOT NULL, `pesos` varchar(255) NOT NULL, `identificador` varchar(55) NOT NULL, PRIMARY KEY (`id`), UNIQUE KEY `identificador` (`identificador`)) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=7 ;
CREATE TABLE IF NOT EXISTS `educacion_testing`.`pac_users` ( `id` INT( 11 ) NOT NULL AUTO_INCREMENT PRIMARY KEY , `version` VARCHAR( 20 ) NOT NULL, `username` VARCHAR( 255 ) NOT NULL, `rol` VARCHAR( 55 ) NOT NULL);
CREATE TABLE IF NOT EXISTS `educacion_testing`.`alumnos` ( `id` INT( 11 ) NOT NULL AUTO_INCREMENT PRIMARY KEY , `cedula` VARCHAR( 256 ) NOT NULL, `nombre` VARCHAR( 255 ) NOT NULL, `apodo` VARCHAR( 255 ) NULL, `telefono` VARCHAR( 255 ) NULL, `celular` VARCHAR( 255 ) NULL, `email` VARCHAR( 255 ) NULL, `direccion` VARCHAR( 500 ) NULL , `version` VARCHAR( 20 ) NOT NULL, `fecha_nacimiento` int(10) NOT NULL, `edad` VARCHAR( 255 ) NULL, `cliente_pac` VARCHAR( 20 ) NOT NULL);
CREATE TABLE IF NOT EXISTS `educacion_testing`.`asistencia` (`sesion` INT NOT NULL, `alumno` INT NOT NULL, `estado` VARCHAR ( 50 ) NOT NULL, `observaciones` VARCHAR ( 255 ) NUll );
CREATE TABLE IF NOT EXISTS `educacion_testing`.`sesiones` ( `id` INT( 11 ) NOT NULL AUTO_INCREMENT PRIMARY KEY , `version` VARCHAR( 20 ) NOT NULL, `fecha` INT( 10 ) NOT NULL , `hora_inicio` varchar( 10 ) NOT NULL , `hora_fin` varchar( 10 ) NOT NULL , `profesor` INT( 5 ) NULL , `curso` INT( 5 ) NOT NULL , `observaciones` TEXT NOT NULL, `incidencias` TEXT NULL, `sin_profesor` INT ( 1 ) NULL, `tarifa` varchar(50) NULL , `owned_by` INT( 5 ) NULL , `estado` INT ( 1 ) NOT NULL, `pagado` INT ( 1 ) NOT NULL);
CREATE TABLE IF NOT EXISTS `educacion_testing`.`examenes` (`id` INT( 11 ) NOT NULL AUTO_INCREMENT PRIMARY KEY , `version` VARCHAR( 20 ) NOT NULL, `titulo` VARCHAR( 255 ) NOT NULL , `fecha` INT( 10 ) NOT NULL , `curso` INT( 11 ) NOT NULL , `profesor` INT (11) NOT NULL, `categoria` VARCHAR (50) NOT NULL, `observaciones` TEXT NOT NULL );
CREATE TABLE IF NOT EXISTS `educacion_testing`.`notas` (`alumno` INT( 10 ) NOT NULL , `examen` INT( 10 ) NOT NULL , `nota` INT( 3 ) NOT NULL , `observaciones` TEXT NOT NULL );
CREATE TABLE IF NOT EXISTS `educacion_testing`.`notas_finales` (`curso` INT NOT NULL, `alumno` INT NOT NULL, `nota` VARCHAR ( 50 ) NOT NULL );
